import pytest

from rlp.utils import (
    big_endian_to_int,
)

from ethereum.utils import sha3

from eth_tester_client.utils import (
    encode_data,
    decode_hex,
)


CONTRACT_BIN = b''


CONTRACT_BIN_RUNTIME = b''

CONTRACT_SOURCE = (
"""contract Math {
    uint public counter;

    event Increased(uint value);

    function increment() public returns (uint) {
        return increment(1);
    }

    function increment(uint amt) public returns (uint result) {
        counter += amt;
        result = counter;
        Increased(result);
        return result;
    }

    function add(int a, int b) public returns (int result) {
        result = a + b;
        return result;
    }

    function multiply7(int a) public returns (int result) {
        result = a * 7;
        return result;
    }

    function return13() public returns (int result) {
        result = 13;
        return result;
    }
}""")


def test_eth_call(client, accounts):
    txn_hash = client.send_transaction(
        _from=accounts[0],
        data=CONTRACT_BIN,
        value=1234,
    )
    txn_receipt = client.get_transaction_receipt(txn_hash)
    contract_address = txn_receipt['contractAddress']

    assert contract_address

    function_sig = encode_data(sha3("return13()")[:4])

    should_be_13 = client.call(
        _from=accounts[0],
        to=contract_address,
        data=function_sig,
    )

    result = big_endian_to_int(decode_hex(should_be_13[2:]))
    assert result == 13
